import { join } from 'desm';
import { execa } from 'execa';
export async function executeVueTsc(args, execaOptions) {
    const vueTscWrapperPath = join(import.meta.url, '../vue-tsc/wrapper.js');
    const { stdout: vueSFCDeclaration } = await execa('node', [vueTscWrapperPath, ...args], execaOptions);
    return vueSFCDeclaration;
}
